import React, { FC, memo, useCallback } from 'react';
import Navigation from '../Navigation/Navigation';
import './Sidebar.scss';
import { joinStrings } from '../utils/utils';
import ArrowRight from '../images/arrows-right.png';

type SidebarProps = {
  isSidebarOpen: boolean;
  setIsSidebarOpen: (isSidebarOpen: boolean) => void;
};

/**
 *  Sidebar component
 */
const Sidebar: FC<SidebarProps> = ({ isSidebarOpen, setIsSidebarOpen }) => {
  const openSidebar = useCallback(() => setIsSidebarOpen(true), []);
  const closeSidebar = useCallback(() => setIsSidebarOpen(false), []);

  return (
    <aside className={joinStrings(['wrapper', isSidebarOpen && 'wrapper--open'])}>
      {isSidebarOpen && <div
        className={joinStrings(['sidebar', isSidebarOpen && 'sidebar--open'])}
      >
        <button className='sidebar__close-btn' onClick={closeSidebar}>
          Close
        </button>
        <div>Anton Stanev</div>
        <p>Senior Frontend Developer</p>
        <Navigation />
      </div>}
      {!isSidebarOpen && (
        <div className='sidebar__open' onClick={openSidebar}>
          <img src={ArrowRight} alt='Open sidebar' />
        </div>
      )}
    </aside>
  );
};

export default memo(Sidebar);
